mapper.select:
select *
from wvp_cloud_record

    AND (app LIKE concat('%',#{query},'%') escape '/' OR stream LIKE
        concat('%',#{query},'%') escape '/')

    and app=#{app}
    and stream=#{stream}
    and end_time >= #{startTimeStamp}
    and start_time <= #{endTimeStamp}
    and call_id = #{callId}
    and media_server_id in

            #{item.id}


    and id in
        #{item}


order by start_time desc

select file_path
from wvp_cloud_record

    and app=#{app}
    and stream=#{stream}
    and end_time >= #{startTimeStamp}
    and start_time <= #{endTimeStamp}
    and call_id = #{callId}
    and media_server_id in
        #{item.id}

select *
from wvp_cloud_record
where collect = false
  and end_time <= #{endTimeStamp}
  and media_server_id = #{mediaServerId}

mapper.update:
update wvp_cloud_record
set collect = #{collect}
where id = #{recordId}
