mapper.update:
UPDATE wvp_user_api_key
SET update_time = #{updateTime}
, app = #{app}
, api_key = #{apiKey}
, expired_at = #{expiredAt}
, username = #{remark}
, enable = #{enable}
WHERE id = #{id}

UPDATE wvp_user_api_key
SET enable = true
WHERE id = #{id}

UPDATE wvp_user_api_key
SET enable = false
WHERE id = #{id}

UPDATE wvp_user_api_key
SET api_key = #{apiKey}
WHERE id = #{id}

UPDATE wvp_user_api_key
SET remark = #{remark}
WHERE id = #{id}

mapper.select:
SELECT COUNT(0)
FROM wvp_user_api_key
WHERE api_key = #{apiKey}
